CREATE DATABASE db_control;

USE db_control;

CREATE TABLE tbl_student (
	id INT AUTO_INCREMENT,
    _name VARCHAR(50),
    cpf CHAR(11),
    date_birth DATE,
    date_reg DATETIME DEFAULT NOW() ,
    CONSTRAINT tbl_student_pk PRIMARY KEY (id),
    CONSTRAINT tbl_student_un UNIQUE (cpf)
);

/* "CONSTRAINT" É UMA FORMA DE RETRINGIR TAL CAMPO COMO A "PRIMARY KEY" E "FOREIGN KEY".
O TEXTO APÓS A "CONSTRAINT" SE REFERE AO APELIDO DADO A ELA, COMO FORMA DE IDENTIFICAR.
"UNIQUE" É UM TIPO DE RESTRIÇÃO QUE DEFINE UM CAMPO COMO "UNICO", SEM SE TORNAR UMA "PRIMARY KEY"*/

CREATE TABLE tbl_access (
	id INT AUTO_INCREMENT,
    id_student INT,
    date_access DATETIME DEFAULT NOW(),
    CONSTRAINT tbl_access_pk PRIMARY KEY(id),
    CONSTRAINT tbl_access_id_student_fk FOREIGN KEY (id_student)
    references tbl_student (id)
);

/* "DATETIME" É UM TIPO DE DADO QUE ARMAZENA DATA E TEMPO NO FORMATO AMERICANO.
A FUNÇÃO "NOW()" SE REFERE A DATA E TEMPO DE "AGORA"*/

