CREATE DATABASE db_sales;

USE db_sales;

CREATE TABLE tbl_category (
	id INT AUTO_INCREMENT,
    _name VARCHAR(25),
    CONSTRAINT tbl_category_pk PRIMARY KEY (id),
    CONSTRAINT tbl_category_un UNIQUE (_name)
);

CREATE TABLE tbl_provider (
	id INT AUTO_INCREMENT,
    _name VARCHAR(50),
    cnpj CHAR(14),
    phone VARCHAR(11),
    CONSTRAINT tbl_provider_pk PRIMARY KEY (id),
    CONSTRAINT tbl_provider_un UNIQUE (cnpj)
);

CREATE TABLE tbl_product (
	id INT AUTO_INCREMENT,
    _name VARCHAR(40),
    _weight NUMERIC(7,4),
    id_provider INT,
    id_category INT,
    CONSTRAINT tbl_product_pk PRIMARY KEY(id),
    CONSTRAINT tbl_product_id_provider_fk FOREIGN KEY (id_provider)
    REFERENCES tbl_provider (id),
    CONSTRAINT tbl_product_id_category_fk FOREIGN KEY (id_category)
    REFERENCES tbl_category (id)
);

CREATE TABLE tbl_price_product (
    id_product INT,
    price NUMERIC(10,2),
    final_duration DATE,
    CONSTRAINT tbl_price_product_pk PRIMARY KEY (id_product),
    CONSTRAINT tbl_price_product_id_product_fk FOREIGN KEY (id_product)
    REFERENCES tbl_product (id)
);
    

CREATE TABLE tbl_uf (
	id INT AUTO_INCREMENT,
    initials CHAR(2),
    CONSTRAINT tbl_uf_pk PRIMARY KEY (id),
    CONSTRAINT tbl_uf_un UNIQUE (initials)
);

CREATE TABLE tbl_city (
	id INT AUTO_INCREMENT,
    _name VARCHAR(40) NOT NULL,
    id_uf INT,
    CONSTRAINT pk_cidade PRIMARY KEY (id),
    CONSTRAINT fk_uf_cidade FOREIGN KEY (id_uf)
    REFERENCES tbl_uf (id)
);

CREATE TABLE tbl_shop (
	id INT AUTO_INCREMENT,
    id_city INT,
    _name VARCHAR(40) NOT NULL,
    CONSTRAINT tbl_shop_pk PRIMARY KEY (id),
    CONSTRAINT tbl_shop_id_city_fk FOREIGN KEY (id_city)
    REFERENCES tbl_city (id)
);

CREATE TABLE tbl_seller (
	id INT AUTO_INCREMENT,
    _name VARCHAR(40) NOT NULL,
    cpf CHAR(11)  NOT NULL,
    phone VARCHAR(11) NULL,
    commission NUMERIC(4,2)  NOT NULL,
    date_reg DATETIME DEFAULT NOW(),
    CONSTRAINT tbl_seller_pk PRIMARY KEY (id),
    CONSTRAINT tbl_seller_un UNIQUE (cpf)
);

CREATE TABLE tbl_client (
	id INT AUTO_INCREMENT,
    _name VARCHAR(40),
    cpf CHAR(11),
    CONSTRAINT tbl_client_pk PRIMARY KEY (id),
	CONSTRAINT tbl_client_un UNIQUE (cpf)
);

CREATE TABLE tbl_sale (
	id INT AUTO_INCREMENT,
    id_shop INT,
    id_seller INT,
    id_client INT,
    date_reg DATETIME DEFAULT NOW(),
    CONSTRAINT tbl_sale_pk PRIMARY KEY (id),
    CONSTRAINT tbl_sale_id_seller_fk FOREIGN KEY (id_seller)
    REFERENCES tbl_seller (id),
    CONSTRAINT tbl_sale_id_client_fk FOREIGN KEY (id_client)
    REFERENCES tbl_client (id),
    CONSTRAINT tbl_sale_id_shop_fk FOREIGN KEY (id_shop)
    REFERENCES tbl_shop (id)
);

CREATE TABLE tbl_sale_item(
	id_sale INT,
    id_product INT,
    qtd INT,
    valor NUMERIC(10,2),
    CONSTRAINT tbl_sale_item_pk PRIMARY KEY (id_sale, id_product),
    CONSTRAINT tbl_sale_item_id_sale_fk FOREIGN KEY (id_sale)
    REFERENCES tbl_sale (id),
    CONSTRAINT tbl_sale_item_id_product_fk FOREIGN KEY (id_product)
    REFERENCES tbl_product (id)
);

